mapper.update:

INSERT INTO tb_file (
      id
    , del_yn
    , reg_id
) VALUES (
      #{fileId}::uuid
    , #{fileDtlYn}
    , #{fileRegId}
 );

INSERT INTO tb_file_dtl (
      id
    , parent_id
    , original_name
    , mimetype
    , file_name
    , destination
    , path
    , size
) VALUES (
      #{fileDtlId}::uuid
    , #{fileParentId}::uuid
    , #{fileOriginalName}
    , #{fileMimeType}
    , #{fileName}
    , #{fileDestination}
    , #{filePath}
    , #{fileSize}::int
)

mapper.select:
SELECT tb_file.id AS file_id , tb_file.reg_id AS file_reg_id , tb_file_dtl.id AS file_dtl_id , tb_file_dtl.parent_id AS file_parent_id , tb_file_dtl.original_name AS file_original_name , tb_file_dtl.mimetype AS file_mime_type , tb_file_dtl.file_name AS file_name , tb_file_dtl.destination AS file_destination , tb_file_dtl.path AS file_path , tb_file_dtl.size AS file_size FROM tb_file , tb_file_dtl WHERE tb_file.id IN
#{data.fileId}
AND tb_file.del_yn = 'N'
